package sec02.exam;

public class ByteOpEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte result1 = 10+20;
		System.out.println(result1);

		byte x = 10;
		byte y = 20;

		int result2 = x+y;
		System.out.println(result2);

		byte value1 = 10;
		int value2 = 100;
		long value3 = 1000L;
		long result = value1 + value2 + value3;
		System.out.println(result);
	}

}
